const animation = [];

function Merge(arr, l, m, r) {
  let i = 0;
  let j = 0;
  let k = l;
  for (let it = 0; it < m - l + 1; it++) left.push(arr[l + i]);
  for (let it = 0; it < r - m; it++) right.push(arr[m + 1 + it]);

  while (i < n && j < m) {
    if (left[i] < right[j]) {
      //   animation.push([arr[k][1], left[i][1]]);
      arr[k] = left[i];
      i++;
      k++;
    } else {
      //   animation.push([arr[k][1], right[j][1]]);
      arr[k] = right[j];
      j++;
      k++;
    }
  }

  while (i < n) {
    // animation.push([arr[k][1], left[i][1]]);
    arr[k] = left[i];
    i++;
    k++;
  }
  while (j < m) {
    // animation.push([arr[k][1], right[j][1]]);
    arr[k] = right[j];
    j++;
    k++;
  }
}
function Sort(arr, n) {
  let curr_size = 0;
  let left_start = 0;
  for (curr_size = 1; curr_size <= n - 1; curr_size = 2 * curr_size) {
    // Pick starting point of different subarrays of current size
    for (left_start = 0; left_start < n - 1; left_start += 2 * curr_size) {
      // Find ending point of left subarray. mid+1 is starting
      // point of right
      let mid = min(left_start + curr_size - 1, n - 1);

      let right_end = min(left_start + 2 * curr_size - 1, n - 1);

      // Merge Subarrays arr[left_start...mid] & arr[mid+1...right_end]
      merge(arr, left_start, mid, right_end);
    }
  }
}
function MergeSort(arr) {
  let myarr = [];
  //   for (let i = 0; i < arr.length; i++) myarr.push([arr[i], i]);
  Sort(arr, arr.length);
  console.log(arr);
  //   return animation;
}

export default MergeSort;
