import React, { Component } from "react";
import Bars from "./components/Bars";
import "./App.css";
import BubbleSort from "./Sorting-algorithms/BubbleSort";
import InsertionSort from "./Sorting-algorithms/InsertionSort";
import QuickSort from "./Sorting-algorithms/QuickSort";

let ch = 1;
class App extends Component {
  constructor() {
    super();
    this.state = {
      arr: [],
    };
  }

  // Generating random number
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  // Generating random array
  getrandomarray() {
    const myarr = [];
    for (let i = 0; i < 100; i++) myarr.push(this.genreateRandom(10, 300));
    this.setState({ arr: myarr });
  }

  componentDidMount() {
    this.getrandomarray();
  }

  CheckSorted(arr, checker) {
    for (let i = 0; i < arr.length; i++) {
      if (arr[i] != checker[i]) return false;
    }
    return true;
  }

  // Animating the algorithms
  AnimateSort(animation) {
    let arr = this.state.arr;
    let checker = this.state.arr.slice();
    checker.sort((a, b) => {
      return a - b;
    });
    // console.log(checker);

    let a, b, h1, h2;
    let bararr = document.getElementsByClassName("bars");

    for (let i = 0; i < animation.length; i++) {
      setTimeout(() => {
        a = animation[i][0];
        b = animation[i][1];
        h1 = arr[a];
        h2 = arr[b];

        bararr[b].style.height = `${h1}px`;
        bararr[a].style.height = `${h2}px`;
        bararr[b].style.backgroundColor = "blue";
        bararr[a].style.backgroundColor = "blue";

        if (animation[i].length == 3)
          bararr[animation[i][2]].style.backgroundColor = "green";

        [arr[a], arr[b]] = [arr[b], arr[a]];

        if (i != 0) {
          a = animation[i - 1][0];
          b = animation[i - 1][1];
          bararr[b].style.backgroundColor = "red";
          bararr[a].style.backgroundColor = "red";
        }

        if (this.CheckSorted(arr, checker)) {
          for (let j = 0; j < arr.length; j++)
            bararr[j].style.backgroundColor = "red";

          for (let j = 0; j < arr.length; j++) {
            // console.log(bararr[j].style.backgroundColor);
            setTimeout(() => {
              bararr[j].style.backgroundColor = "cyan";
            }, j * 10);
          }
        }

      }, i * 50);
    }

    // console.log(document.getElementsByClassName("bars"));
  }

  // Calling The algorithms
  bubbleSort() {
    let arr = this.state.arr.slice(); //Getting a new array
    const animation = BubbleSort(arr);
    this.AnimateSort(animation);
  }

  insertionSort() {
    let arr = this.state.arr.slice();
    const animation = InsertionSort(arr);
    this.AnimateSort(animation);
  }

  quickSort() {
    let arr = this.state.arr.slice();
    const animation = QuickSort(arr);
    this.AnimateSort(animation);
  }

  //All the components that are being rendered
  render() {
    const barsheight = this.state.arr.map((val, inx) => {
      return <Bars height={val} key={inx} class="bars" />;
    });

    return (
      <div>
        <div className="bars-container">{barsheight}</div>
        <button
          onClick={() => {
            this.bubbleSort();
          }}
        >
          Bubble Sort
        </button>
        <button
          onClick={() => {
            this.insertionSort();
          }}
        >
          Insertion Sort
        </button>
        <button
          onClick={() => {
            this.quickSort();
          }}
        >
          Quick Sort
        </button>
      </div>
    );
  }
}

export default App;
