import React, { Component } from "react";
import Bars from "./components/Bars";

class App extends Component {
  constructor() {
    super();
    this.state = {
      intitialarr: [],
    };
    this.componentDidMount = this.componentDidMount.bind(this)
    // console.log("const")
  }
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  getrandomarray(){
    const arr=[];
    for(let i=0; i<10; i++)
        arr.push(this.genreateRandom(1,11))
    return arr
  }

  componentDidMount() {
    // console.log(this.genreateRandom(10, 11));
    const newrandomarray=this.getrandomarray()
    this.setState((prevstate)=>{
      // console.log(prevstate.intitialarr)
      // prevstate.intitialarr=newrandomarray
      return {intitialarr: newrandomarray}
    })
  }

  render() {
    return <div></div>;
  }
}

export default App;
