const animation=[];

function Sort(arr, n){
    if(n<2)
        return;
    let mid=n/2;
}
function MergeSort(arr){
    let myarr=[];
    for(let i=0; i<arr.size(); i++)
        myarr.push([arr[i],i]);
    Sort(myarr,myarr.length)
    return animation;
}

export default MergeSort;
