import React, { Component } from "react";
import Bars from "./components/Bars";

class App extends Component {
  constructor() {
    super();
    this.state = {
      arr: [],
    };
    this.getrandomarray = this.getrandomarray.bind(this);
    // console.log("const")
  }
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  getrandomarray() {
    const myarr = [];
    for (let i = 0; i < 10; i++) myarr.push(this.genreateRandom(1, 11));
    this.setState({arr: myarr})
  }

  componentDidMount() {
    this.getrandomarray()
  }

  temp(){
    [arr[0],arr[9]]=[arr[9],arr[0]]
  }

  render() {
    // this.temp()
    const barsheight = this.state.arr.map((val) => {
      return <Bars bar={val} />;
    });

    return (
      <div>
        {barsheight}
        <button onClick="{temp}"></button>
      </div>
    );
  }
}

export default App;
