import React, { Component } from "react";
import Bars from "./components/Bars";

class App extends Component {
  constructor() {
    super();
    this.state = {
      intitialarr: [],
    };

  }
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  getrandomarray(){
    const arr=[];
    for(let i=0; i<10; i++)
        arr.push(this.genreateRandom(1,11))
  }

  componentDidMount() {
    console.log(this.genreateRandom(10, 11));
    const newrandomarray=this.getrandomarray()
    this.setState(()=>{
      intitialarr= newrandomarray;
    })
  }

  render() {
    return <div></div>;
  }
}

export default App;
