let animation = [];

function Sort(arr, st, en) {
  if (st >= en) return;
  let pivot = partition(v, st, en);
  quickSort(v, st, pivot - 1);
  quickSort(v, pivot + 1, en);
}
function QuickSort(arr) {
  Sort(arr, 0, arr.length);
  return animation;
}

export default QuickSort;
