import React, { Component } from "react";
import Bars from "./components/Bars";
import "./App.css";
import BubbleSort from "./Sorting-algorithms/BubbleSort";

class App extends Component {
  constructor() {
    super();
    this.state = {
      arr: [],
    };
  }

  // Generating random number
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  // Generating random array
  getrandomarray() {
    const myarr = [];
    for (let i = 0; i < 100; i++) myarr.push(this.genreateRandom(10, 300));
    this.setState({ arr: myarr });
  }

  componentDidMount() {
    this.getrandomarray();
  }

  // Animating the algorithms
  AnimateSort(){

  }
  // Calling The algorithms
  bubbleSort() {
    let arr = this.state.arr.slice();
    const animation = BubbleSort(arr);
    // console.log(this.state.arr);
    arr = this.state.arr;
    let a, b;
    for (let i = 0; i < animation.length; i++) {
      setTimeout(() => {
        a = animation[i][0];
        b = animation[i][1];
        if (arr[a] > arr[b]) {
          [arr[a], arr[b]] = [arr[b], arr[a]];
          this.setState({ ...arr });
        }
        // console.log(this.state.arr);
      }, i * 10);
    }
  }

  render() {
    const barsheight = this.state.arr.map((val, inx) => {
      return <Bars bar={val} key={inx} />;
    });

    return (
      <div>
        <div className="bars-container">{barsheight}</div>
        <button
          onClick={() => {
            this.bubbleSort();
          }}
        >
          Bubble Sort
        </button>
      </div>
    );
  }
}

export default App;
