let animation = [];
function sort(arr)
	{
		let n = arr.length;

		// Build heap
		for (let i = Math.floor(n / 2) - 1; i >= 0; i--)
			heapify(arr, n, i);

		// One by one extract an element from heap
		for (let i = n - 1; i > 0; i--) {
			// Move current root to end
			let temp = arr[0];
			arr[0] = arr[i];
			arr[i] = temp;

			heapify(arr, i, 0);
		}
	}
