import React, { Component } from "react";
import Bars from "./components/Bars";

class App extends Component {
  constructor() {
    super();
    this.state = {
      arr: [],
    };
    this.componentDidMount = this.componentDidMount.bind(this);
    this.temp = this.temp.bind(this);
    // console.log("const")
  }
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  getrandomarray() {
    const arr = [];
    for (let i = 0; i < 10; i++) arr.push(this.genreateRandom(1, 11));
    this.setState({arr})
  }

  componentDidMount() {
    this.getrandomarray()
  }

  render() {
    // this.temp()
    const barsheight = this.state.intitialarr.map((val) => {
      return <Bars bar={val} />;
    });

    return (
      <div>
        {barsheight}
        {/* <button onClick={this.temp}>Button</button> */}
      </div>
    );
  }
}

export default App;
