import React, { Component } from "react";
import Bars from "./components/Bars";

class App extends Component {
  constructor() {
    super();
    this.state = {
      intitialarr: [],
    };
    this.componentDidMount = this.componentDidMount.bind(this);
    this.temp = this.temp.bind(this);
    // console.log("const")
  }
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  getrandomarray() {
    const arr = [];
    for (let i = 0; i < 10; i++) arr.push(this.genreateRandom(1, 11));
    return arr;
  }

  componentDidMount() {
    const newrandomarray = this.getrandomarray();
    this.setState((prevstate) => {
      return { intitialarr: newrandomarray };
    });
  }

  temp() {
    if (this.intitialarr.lenght === 0) return;
    const newrandomarray = this.state.intitialarr;
    [newrandomarray[0], newrandomarray[9]]=[newrandomarray[0], newrandomarray[9]];
    this.setState((prevstate) => {
      return { intitialarr: newrandomarray };
    });
  }

  render() {
    const barsheight = this.state.intitialarr.map((val) => {
      return <Bars bar={val} />;
    });
    return <div>{barsheight}</div>;
  }
}

export default App;
