import React, { Component } from "react";
import Bars from "./components/Bars";
import "./App.css"

class App extends Component {
  constructor() {
    super();
    this.state = {
      arr: [],
    };
    this.getrandomarray = this.getrandomarray.bind(this);
    // console.log("const")
    this.temp = this.temp.bind(this);
  }

  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  getrandomarray() {
    const myarr = [];
    for (let i = 0; i < 10; i++) myarr.push(this.genreateRandom(10, 30));
    this.setState({ arr: myarr });
  }

  componentDidMount() {
    this.getrandomarray();
  }


  render() {
    // this.temp()
    const barsheight = this.state.arr.map((val) => {
      return <Bars bar={val} />;
    });

    return (
      <div className="bars-container">
        {barsheight}
      </div>

    );
  }
}

export default App;
