import React, { Component } from "react";
import Bars from "./components/Bars";

class App extends Component {
  constructor() {
    super();
    this.state = {
      arr: [],
    };
    this.getrandomarray = this.getrandomarray.bind(this);
    // console.log("const")
    this.temp = this.temp.bind(this);
  }
  genreateRandom(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  getrandomarray() {
    const myarr = [];
    for (let i = 0; i < 100; i++) myarr.push(this.genreateRandom(1, 101));
    this.setState({ arr: myarr });
  }

  componentDidMount() {
    this.getrandomarray();
  }

  temp() {
    const newarr = this.state.arr;
    for (let i = 0; i < newarr.length; i++) {
      for (let j = 0; j < newarr.length; j++) {
        if (newarr[i] > newarr[j]) {
          setTimeout(() => {
            [newarr[i], newarr[j]] = [newarr[j], newarr[i]];
            console.log(this.state.arr);
            this.setState({ arr: newarr });
          }, i * 100);
        }
      }
    }

    // setTimeout(() => {
    // [newarr[0],newarr[9]]=[newarr[9],newarr[0]]
    //   console.log(this.state.arr)
    //   this.setState({arr: newarr})
    // }, 1000);
  }

  render() {
    // this.temp()
    const barsheight = this.state.arr.map((val) => {
      return <Bars bar={val} />;
    });

    return (
      <div>
        {barsheight}
        <button onClick={() => this.temp()}>Button</button>
      </div>
    );
  }
}

export default App;
