const animation = [];

function Merge(left, right, n, m, arr) {
  let i = 0,
    j = 0,
    k = 0;
  while (i < n && j < m) {
    if (left[i] < right[j]) {
      animation.push([arr[k][1], left[i][1]]);
      arr[k] = left[i];
      i++;
      k++;
    } else {
      animation.push([arr[k][1], right[k][1]]);
      arr[k] = right[j];
      j++;
      k++;
    }
  }

  while (i < n) {
    animation.push([arr[k][1], left[i][1]]);
    arr[k] = left[i];
    i++;
    k++;
  }
  while (j < m) {
    animation.push([arr[k][1], right[j][1]]);
    arr[k] = right[j];
    j++;
    k++;
  }
}
function Sort(arr, n) {
  if (n < 2) return;
  let mid = n / 2;
  let left, right;
  for (let i = 0; i < mid; i++) left.push(arr[i]);
  for (let j = mid; j < n; j++) right.push(arr[j]);
  Sort(left, left.length);
  Sort(right, right.size());
  Merge(left, right, left.length, right.length, arr);
}
function MergeSort(arr) {
  let myarr = [];
  for (let i = 0; i < arr.size(); i++) myarr.push([arr[i], i]);
  Sort(myarr, myarr.length);
  return animation;
}

export default MergeSort;
